package api.hbm.energy;

import java.util.List;

public interface IEnergySource {
	
	void ffgeuaInit();
	/**
	 * If you want the energy that is generated by the energy source
	 * to be transferred by the copper cables, use this. To use this,
	 * call the ffgeua method in {@code HbmUtils}*/
	void ffgeua(int x, int y, int z, boolean newTact);
	/**
	 * This controls how often energy gets drawn into conductors per tick*/
	boolean getTact();
	/**Returns the amount of power the tile entity has*/
	long getSPower();
	void setSPower(long i);
	List<IEnergyConsumer> getList();
	void clearList();
}
